export default {
  data: () => {
    return {
      mode: 'lightmode'
    };
  },
  created() {
    const bodyElement = document.body;
    const mode = localStorage.getItem("theme");
    if (mode) {
      this.mode = mode;
      bodyElement.setAttribute("class", mode);
    }
  },
  methods: {
    toggle(mode) {
      this.mode = mode;
      const bodyElement = document.body;
      bodyElement.setAttribute("class", mode);
      localStorage.setItem("theme", mode);
    }
  }
};
